import React from "react";
import { useState } from "react";

const Search = ({ query, querySent, onSearch }) => {
  console.log("query: ", query);
  const [message, setMessage] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [fact, setFact] = useState({
    items: [],
    isLoaded: false,
  });

  const handleChange = (event) => {
    event.preventDefault();
    setMessage(event.target.value);

    //console.log("value is:", event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setSearchQuery(event.target.queryField.value);
    onSearch({ query: event.target.queryField.value, querySent: querySent });
    newCatFact();
    walmartResponse();
  };

  const newCatFact = () => {
    fetch("https://catfact.ninja/fact")
      .then((res) => res.json())
      .then((json) => {
        setFact({
          items: json,
          isLoaded: true,
        });
        console.log(json.fact);
      });
  };

  /*const ebayResponse = () => {
    fetch(
      "https://svcs.ebay.com/services/search/FindingService/v1?OPERATION-NAME=findItemsByKeywords&SERVICE-VERSION=1.0.0&SECURITY-APPNAME=YourAppID&RESPONSE-DATA-FORMAT=XML&REST-PAYLOAD&keywords=harry%20potter%20phoenix"
    )
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
      });
  };*/

  const ebayResponse = () => {
    const requestOptions = {
      method: "POST",
      //headers: { 'Content-Type': 'application/json' },
      //body: JSON.stringify({ title: 'React POST Request Example' })
    };

    fetch(
      "https://svcs.sandbox.ebay.com/services/search/FindingService/v1?OPERATION-NAME=findItemsByKeywords&SERVICE-VERSION=1.0.0&SECURITY-APPNAME=YourAppID&PhilippB-Fiindii-SBX-df2241f4a-e35c32c6RESPONSE-DATA-FORMAT=XML&REST-PAYLOAD=true& paginationInput.entriesPerPage=100&keywords=iphone"
    )
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
      });
  };

  ebayResponse();

  return (
    <div className="topnav">
      <h1>query: {query}</h1>
      <h1>searchQuery: {searchQuery}</h1>
      <h1>cat fact: {fact.items.fact}</h1>

      <form onSubmit={(e) => handleSubmit(e)}>
        <input
          id="queryField"
          type="text"
          placeholder="Search.."
          onChange={(e) => handleChange(e)}
        />
      </form>
    </div>
  );
};

export default Search;
